import 'next-auth';
import 'next-auth/jwt';

declare module 'next-auth' {
  interface Session {
    user: {
      id: string;
      email: string;
      name?: string | null;
      userType?: string;
      merchantId?: string;
      merchantIds?: string[];
    };
  }

  interface User {
    id: string;
    email: string;
    name?: string | null;
    userType?: string;
    merchantId?: string;
    merchantIds?: string[];
  }
}

declare module 'next-auth/jwt' {
  interface JWT {
    userId?: string;
    userType?: string;
    merchantId?: string;
    merchantIds?: string[];
  }
}
